// Extrai campos do payload Chatwoot
const body = $input.item.json.body || $input.item.json;
const event = body.event;
const conv = body.conversation || {};
const status = conv.status || body.status;
const convId = conv.id || body.id || (body.conversation && body.conversation.id);

// Procura telefone em múltiplos caminhos possíveis no payload do Chatwoot
let rawPhone = (
  conv.meta?.sender?.phone_number || 
  body.meta?.sender?.phone_number || 
  body.sender?.phone_number || 
  (body.contact && body.contact.phone_number) ||
  ''
).replace(/\D/g,'').replace(/^\+/, '');

// Normaliza para incluir o código do país se faltar
const phone = rawPhone.startsWith('55') ? rawPhone : (rawPhone ? '55' + rawPhone : '');

const messageContent = body.content;
const messageType = body.message_type;
const assignee = conv.meta?.assignee?.name || body.meta?.assignee?.name;

// Filtro de segurança: se não temos telefone e não é um evento que processamos, ignoramos
if (!phone && event !== 'message_created' && event !== 'conversation_status_changed') {
  return [];
}

return [{ json: { event, status, phone, convId, assignee, messageContent, messageType, raw: body } }];

const rows = $input.all();
const phone = $node['Extrair Evento'].json.phone;
const currentStatus = rows.length > 0 ? rows[0].json.current_status : null;
const currentModule = rows.length > 0 ? rows[0].json.current_module : 1;

// IDEMPOTÊNCIA: Se já está em modo 'bot', não processar novamente
// (Chatwoot pode disparar eventos duplicados)
if (currentStatus === 'bot') {
  console.log(`[HANDOFF] Phone ${phone} already 'bot' — skipping duplicate event`);
  return []; // para execução
}

return [{ json: { phone, currentModule: currentModule || 1 } }];

