BEGIN;
-- 1. Atualizar students
UPDATE students
SET attendance_status = 'bot', updated_at = NOW()
WHERE phone = '{{ $json.phone }}';

-- 2. Registrar handoff_control (idempotente via ON CONFLICT)
INSERT INTO handoff_control (phone, status, last_handoff)
VALUES ('{{ $json.phone }}', 'bot', NOW())
ON CONFLICT (phone) DO UPDATE
  SET status = 'bot', last_handoff = NOW();

-- 3. Encerrar sessão de suporte aberta (se houver)
UPDATE support_sessions
SET ended_at = NOW(), bot_resumed = TRUE
WHERE student_id = (SELECT id FROM students WHERE phone = '{{ $json.phone }}')
  AND ended_at IS NULL;

COMMIT;

parameters.jsCode:
const phone = $node['Extrair Evento'].json.phone;
const rows = $input.all();

let name = 'Aluno';
let currentModule = 1;
let totalModules = 1;
let completedModules = [];
let courseName = 'Kreativ Educação';
let portalToken = null;
let label = 'em-andamento';

if (rows && rows.length > 0 && rows[0].json.phone) {
  const s = rows[0].json;
  name = s.name || 'Aluno';
  currentModule = s.current_module || 1;
  totalModules = s.total_modules || 1;
  completedModules = s.completed_modules || [];
  courseName = s.course_name || 'Kreativ Educação';
  portalToken = s.portal_token || null;
}

const nCompleted = Array.isArray(completedModules) ? completedModules.length : 0;
const courseComplete = nCompleted >= totalModules && totalModules > 0;

// Determinar label do Chatwoot pós-atendimento
if (courseComplete) {
  label = 'concluido';
} else if (currentModule <= 5) {
  label = `modulo-${currentModule}`;
} else {
  label = 'em-andamento';
}

// Montar mensagem personalizada por caso
let message;
if (!rows || rows.length === 0 || !rows[0].json.phone) {
  // Caso 0: Aluno não encontrado no DB
  message = '✅ *Seu atendimento foi encerrado!*\n\nPara começar ou retomar o curso, envie *OI*.';
  label = 'novo-lead';
} else if (courseComplete) {
  // Caso 3: Curso completo
  const portalLink = portalToken
    ? `\n\n📊 Veja seus certificados em:\nhttps://portal.extensionista.site/aluno/${portalToken}`
    : '';
  message = `✅ *Atendimento encerrado!* Bom ver você, ${name}! 😊\n\nParabéns — você já concluiu o curso *${courseName}*! 🏆${portalLink}\n\nDigite *OI* se precisar de mais alguma coisa.`;
} else if (nCompleted === 0 && currentModule === 1) {
  // Caso 1: Aluno não iniciou nenhum módulo
  message = `✅ *Atendimento encerrado!* Olá, ${name}! 👋\n\nPara começar seu aprendizado no curso *${courseName}*, responda:\n👉 *MODULO* — acessar o Módulo 1\n👉 *PROGRESSO* — ver seu painel`;
} else {
  // Caso 2: Aluno em andamento
  const portalLink = portalToken
    ? `\n\n📊 Acompanhe seu progresso: https://portal.extensionista.site/aluno/${portalToken}`
    : '';
  message = `✅ *Atendimento encerrado!* Olá, ${name}! 👋\n\nPode continuar de onde parou no curso *${courseName}*:\n👉 *MODULO* — continuar no Módulo ${currentModule}\n👉 *QUIZ* — refazer a avaliação do módulo atual\n👉 *PROGRESSO* — ver todo seu andamento${portalLink}`;
}

return [{ json: { phone, message, label } }];

