try {
  const body = $input.item.json.body ?? $input.item.json;
  const phone = String(body.phone ?? '').replace(/\D/g, '');
  const moduleNumber = parseInt(String(body.moduleNumber ?? body.moduleId ?? '0'), 10);

  if (!phone) {
    return [{ json: { error: true, message: 'phone √© obrigat√≥rio', phone } }];
  }

  return [{ json: { phone, moduleNumber, error: false } }];
} catch (e) {
  return [{ json: { error: true, message: e.message, phone: '' } }];
}

try {
  const extracted = $('Extrair e validar').item.json;
  const data = $input.all()[0]?.json ?? {};
  const certId = 'KRV-' + Math.random().toString(36).substring(2,10).toUpperCase();
  const date = new Date().toLocaleDateString('pt-BR', { year:'numeric', month:'long', day:'numeric' });
  const studentName = data.name ?? extracted.phone;
  const moduleName = data.module_name ?? 'M√≥dulo Conclu√≠do';
  const courseName = data.course_name ?? 'Kreativ Educa√ß√£o';
  const phone = data.phone ?? extracted.phone;

  const html = `<!DOCTYPE html><html><head><meta charset='utf-8'><style>body{font-family:'Georgia',serif;text-align:center;padding:60px;border:8px double #2c3e50;margin:40px;background:#fefefe;}h1{color:#2c3e50;font-size:2.5em;}h2{color:#16a085;}.info{font-size:1.2em;margin:20px 0;}.code{color:#7f8c8d;font-size:0.9em;margin-top:40px;}</style></head><body><h1>Certificado de Conclus√£o</h1><h2>Kreativ Educa√ß√£o</h2><p class='info'>Certificamos que <strong>${studentName}</strong> concluiu com √™xito o m√≥dulo <strong>${moduleName}</strong> do curso <strong>${courseName}</strong> em ${date}.</p><p class='code'>C√≥digo de autenticidade: ${certId}</p></body></html>`;

  const nameEncoded = encodeURIComponent(studentName);
  const moduloEncoded = encodeURIComponent(moduleName);
  const cursoEncoded = encodeURIComponent(courseName);
  const dateEncoded = encodeURIComponent(date);
  const certPortalUrl = `https://portal.extensionista.site/certificado/${certId}?name=${nameEncoded}&modulo=${moduloEncoded}&curso=${cursoEncoded}&data=${dateEncoded}`;

  return [{ json: { html, certId, certPortalUrl, phone, studentName, error: false } }];
} catch (e) {
  return [{ json: { error: true, message: 'Erro ao gerar certificado: ' + e.message, phone: '' } }];
}
